import styled from "styled-components";
import { GlobalCentralizeDiv } from "../../stylesGlobal";

const textOption = [
  { id: 1, name: 'Plan Amigo', link: 'friend' },
  { id: 2, name: 'Regalo', link: 'sport' },
  { id: 3, name: 'App', link: '/' },
  { id: 4, name: 'Preguntas', link: 'faq' },
];

const OptionsList = styled.li`
  padding: 0 2rem;
  cursor: pointer;
  display: flex;
  justify-content: center;
  align-items: center;
  list-style: none;

  a {
    text-decoration: none;
    color: #000;
    text-align: center;
    font-size: 1rem;
    font-weight: 400;
    
    &:hover {
      color: #fff;
      cursor: pointer;
    }
  }

  @media (max-width: 481px) {
    
    a {
      display: none;
    }

    

  }

`;


const Div = styled.div`

  .material-icons {
    display: none;
  }

  @media (max-width: 481px) {
    
    i {
      display: none;
    }

  }

`

function OptionsHeader() {

  const pageVerification = window.location.pathname === "/";

  return (
    <GlobalCentralizeDiv>
      <Div><i className="material-icons">menu</i></Div>
      {textOption.map((text, indice) => <OptionsList key={indice}><a href={pageVerification && text.link === "/" ? "#app" : text.link}>{text.name}</a></OptionsList>)}
    </GlobalCentralizeDiv>
  )
};

export default OptionsHeader;
