import styled from "styled-components";
import { GlobalTitle } from "../../../stylesGlobal";

const Div = styled.div`
    text-align: center;
    max-width: 1080px;
    width: 100%;
`;

const GlobalTitleSec4 = styled(GlobalTitle)`
    margin: 4rem 0 2rem 0;

    @media (max-width: 481px) {
        
    }
`;

const SubTitle = styled.h2`
    text-transform: uppercase;
    font-weight: 400;
    margin: 0.5rem 0rem;
    font-size: 1.5rem;
    margin: 0 0 3rem 0;
    line-height: 1.5;
    width: 100%;

    @media (max-width: 481px) {
        font-size: 1.2rem;
        margin: 0 5rem 1.5remm;
    }
`;

function MainSecFriend() {
    return (
        <Div>
            <GlobalTitleSec4>
            nuevo plan amigo
            </GlobalTitleSec4>
            <SubTitle>
            Invita a un amigo y gana 15€ para ti y para él. Trae a tantos como quieras, ¡Es ilimitado!
            </SubTitle>
        </Div>
    )
};

export default MainSecFriend;
