import React from "react";
import ReactDOM from "react-dom/client";
import App from "./App";
import { createGlobalStyle } from "styled-components";
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import PageFriend from "./pages/pagefriend/pagefriend";
import Sport from "./pages/sport/sport";
import Faq from "./pages/faq/faq"; 

const GlobalStyle = createGlobalStyle`
  * {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
    font-family: 'Roboto', sans-serif;


    /* Estilizando a barra de rolagem */
    ::-webkit-scrollbar {
      width: 10px; /* Largura da barra de rolagem */
    }

    /* Estilizando o track da barra de rolagem (a área cinza) */
    ::-webkit-scrollbar-track {
      background: #fff; /* Gradiente de cima para baixo */
    }

    /* Estilizando o thumb da barra de rolagem (a alça da barra de rolagem) */
    ::-webkit-scrollbar-thumb {
      background: linear-gradient(to bottom, #81A4FC 0%, #A174FC 100%); /* Cor do thumb */
      border-radius: 5px; /* Arredondamento do thumb */
    }

    /* Estilizando o thumb da barra de rolagem quando estiver sendo hover */
    ::-webkit-scrollbar-thumb:hover {
      background: linear-gradient(to bottom, #81A4FC 0%, #A174FC 100%); /* Cor do thumb em hover */
    }

    /* Aplicando o border radius na barra de rolagem */
    ::-webkit-scrollbar {
      border-radius: 10px; /* Border radius */
    }

}
`;

const router = createBrowserRouter([
  {
    path: "/",
    element: <App />,
  },
  {
    path: "friend",
    element: <PageFriend />,
  },
  {
    path: "sport",
    element: <Sport />,
  },
  {
    path: "faq",
    element: <Faq />
  },
]);

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <GlobalStyle />
    <RouterProvider router={router} />
  </React.StrictMode>
);
