import styled from "styled-components";
import { useState } from "react";
import PopDesign from "../../../PopUp";
import { GlobalButton, GlobalTitle } from "../../../stylesGlobal";

const MainSection = styled.div`
    text-align: center;
    max-width: 1080px;
    width: 100%;
`;

const DivText = styled.div`
    margin: 0 auto;
`

const GlobalTitleSec2 = styled(GlobalTitle)`
    margin: 2.5rem;
    line-height: 1.5;

    @media (max-width: 481px) {
        font-size: 1.3rem;
        margin: 1.5rem 0;
    }
`;

const SubTitle = styled.h2`
    text-transform: uppercase;
    font-weight: 400;
    margin: 0.5rem 0rem;
    font-size: 1.5rem;
    line-height: 1.5;
`;

const GlobalButtonCTA = styled(GlobalButton)`
    margin: 1.5rem 0rem;
`;

function MainSecButtonCTA() {

    const [open, setOpen] = useState(false)

    const popUpOpen = () => {
        setOpen(true);
    }

    const popUpClose = () => {
        setOpen(false);
    }

    return (
        <MainSection >
            <DivText>
                <GlobalTitleSec2>
                    ¿Quieres saber si tenemos cobertura de fibra en tu hogar?
                </GlobalTitleSec2>
                <SubTitle>
                    Míralo aquí y contrata tu tarifa de fibra +móvil o sólo fibra de forma sencilla
                </SubTitle>
            </DivText>
            <GlobalButtonCTA onClick={popUpOpen}>
                Comprobar cobertura
            </GlobalButtonCTA>
            {open && <PopDesign onClose={popUpClose} />}
        </MainSection >
    )
};

export default MainSecButtonCTA;
