import styled from 'styled-components';

const InputWithStyled = styled.input`
    background: transparent;
    border: 1px solid #FFF;
    margin: 1.25rem 8.75rem;
    border-radius: 3.125rem;
    width: 12.5rem;
    color: #FFF;
    font-size: 1rem;
    margin-bottom: 10px;
    text-align: center;

    &::placeholder {
        color: #FFF;
        font-size: 1rem;
    }

`

export default InputWithStyled
