import style from "styled-components";
import { useState } from 'react';
import PopDesign from '../PopUp';

const iconOption = ["person", "shopping_cart"];

const Icons = style.ul`
    align-items: center;
    display: flex;
    justify-content: center;
`;

const IconList = style.li`
    align-items: center;
    cursor: pointer;
    display: flex;
    justify-content: center;
    width: 1.563rem;
    margin: 0 1.4rem;
    

    button {border: none;
        background-color: none;
        &:hover {
            color: #fff;
            cursor: pointer;
        }
    }
`;

function OptionsIconHeader() {

    const [open, setOpen] = useState(false);

    const popUpOpen = () => {
        setOpen(true);
    }

    const popUpClose = () => {
        setOpen(false);
    }


    return (
        <Icons>
            <IconList>
                <button className="material-icons" onClick={popUpOpen}>{iconOption[0]}</button>
                <button className="material-icons" onClick={popUpOpen}>{iconOption[1]}</button>
            </IconList>
            {open && <PopDesign onClose={popUpClose}/>}
        </Icons>
    )
};

export default OptionsIconHeader;
