import styled from "styled-components";
import { useState } from "react";
import PopDesign from "../../../PopUp";
import { GlobalButton, GlobalTitle } from "../../../stylesGlobal";

const MainSection = styled.div`
    text-align: center;
    max-width: 1080px;
    width: 100%;
`;

const GlobalTitleSec2 = styled(GlobalTitle)`
    margin: 2.5rem;
    line-height: 1.5;

    @media (max-width: 481px) {
        margin: 0rem;
        padding: 1.8rem 0.2rem 0.5rem;
    }
`;

const SubTitle = styled.h2`
    font-weight: 400;
    margin: 0.5rem 0rem;
    font-size: 1.5rem;
    line-height: 1.5;

    @media (max-width: 481px) {
        font-size: 0.97rem;
        padding: 0rem 3rem;
    }
`;

const GlobalButtonCTA = styled(GlobalButton)`
    margin: 1.5rem 0rem;

    @media (max-width: 481px) {
        width: 170px;
        height: 40px;
        font-size: 0.9rem;
        font-weight: 200;
        margin: 0 0 1.5rem;
    }
`;

function MainSecButtonCTA() {

    const [open, setOpen] = useState(false)

    const popUpOpen = () => {
        setOpen(true);
    }

    const popUpClose = () => {
        setOpen(false);
    }

    return (
        <MainSection >
            
                <GlobalTitleSec2>
                    ¿Quieres saber si tenemos cobertura de fibra en tu hogar?
                </GlobalTitleSec2>
                <SubTitle>
                    Míralo aquí y contrata tu tarifa de fibra +móvil o sólo fibra de forma sencilla
                </SubTitle>
            
            <GlobalButtonCTA onClick={popUpOpen}>
                Comprobar cobertura
            </GlobalButtonCTA>
            {open && <PopDesign onClose={popUpClose} />}
        </MainSection >
    )
};

export default MainSecButtonCTA;
