import styled from "styled-components";
const textOption = [
  { id: 1, name: 'Regalo', link: 'regalo' },
  { id: 2, name: 'Preguntas Frecuentes', link: 'preguntas-frecuentes' },
  { id: 3, name: 'Como Funciona', link: 'como-funciona' },
  { id: 4, name: 'Acerca de', link: 'acerca-de' },
];

const Options = styled.ul`
  display: flex;
  justify-content: center;
  align-items: center;
`

const OptionsList = styled.li`
  padding: 0 2rem;
  min-width: 7.5rem;
  cursor: pointer;
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  font-size: 1rem;
  font-weight: 500;
  height: 100%;
  list-style: none;

  p {
    &:hover {
      color: #fff;
      cursor: pointer;
    }
  }
`

function OptionsHeader() {
  return (
    <Options>
      {textOption.map((text, key) => <OptionsList key={key} ><a>{text}</a></OptionsList>)}
    </Options>
  )
};

export default OptionsHeader;
