import styled from "styled-components";

const textOption = ['Tarifas móvil ', 'Tarifas Fibra', 'Tarifas fibra + Móvil', 'atención al Cliente'];
const TMovil = ['Móvil 24GB', 'Móvil 50GB', 'Móvil 100GB']
const TFibra = ['Fibra 600Mb', 'Fibra 300Mb', 'Fibra 100Mb']
const TFibraMovil = ['600Mb+55GB', '300Mb+2líneas*25GB', '600Mb+25GB', '300Mb+55GB', '300Mb+10GB']
const TAtencion = ['Contacto',
    'Centro de ayuda',
    'Lámanos al 1777'
    ]

const Options = styled.ul`
  display: flex;
  width: 500px;
  justify-content: center;
  align-items: center;
`

const OptionsList = styled.li`
  padding: 0 2rem;
  min-width: 7.5rem;
  cursor: pointer;
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  font-size: 1rem;
  height: 100%;
  list-style: none;
`

function OptionsHeader() {
  return (
    <Options>
      {textOption.map((text, key) => <OptionsList key={key}><p>{text}</p></OptionsList>)}
    </Options>
  )
};

export default OptionsHeader;
