import styled from "styled-components";

export const Title = styled.h2`
    width: 100%;
    padding: 30px 0;
    background-color: #FFF;
    color: ${props => props.color || '#FFF' };
    font-size: ${props => props.fontSize || '1rem'};
    text-align: ${props => props.align || 'c'};
    margin: 0;
`
