import styled from "styled-components";

export const Title = styled.h2`
    width: 100%;
    padding: 30px 0;
    background-color: #FFF;
    color: ${props => props.cor || '#FFF' };
    font-size: 36px;
    text-align: center;
    margin: 0;
`
