import InputWithStyled from '../Input';
import styled from 'styled-components';


const SearchContainer = styled.section`
    background-image: linear-gradient(90deg, #002F52 35%, #326589 165%);
    color: #FFF;
    text-align: center;
    padding: 5.313rem 0;
    heigth: 16.875rem;
    width: 100%;
`
const Title = styled.h2`
    color: #FFF;
    font-size: 2.25REM;
    text-align: center;
    width: 100%;
`

const SubTitle = styled.h3`
    font-size: 1rem; 
    font-wigth: 500;
    margin-bottom: 2.5rem;
`

function Search(){
    return (
        <SearchContainer>
            <Title>You already know where you'd like to start?</Title>
            <SubTitle>Find your book on our shelf.</SubTitle>
            <InputWithStyled placeholder="Write your next read"/>
        </SearchContainer>
    )
}

export default Search
