import { GlobalTitle } from "../../../stylesGlobal";
import styled from "styled-components";

const MainSection = styled.div`
    text-align: center;
    max-width: 1080px;
    width: 100%;
`;

const GlobalTitleSec2 = styled(GlobalTitle)`
    margin: 2.5rem;
    line-height: 1.5;
    
    @media (max-width: 768px) {
        margin: 0rem;
        padding: 2rem 0.2rem 0.8rem;
    }

    @media (max-width: 481px) {
        margin: 0rem;
        padding: 1.8rem 0.2rem 0.5rem;
    }
`;

const SubTitle = styled.h2`
    font-weight: 400;
    margin: 0.5rem 0rem 0.8rem;
    font-size: 1.5rem;
    line-height: 1.5;

    @media (max-width: 1024px) {
        font-size: 22px;
    }

    @media (max-width: 768px) {
        font-size: 19px;
        padding: 0 2rem;
    }

    @media (max-width: 481px) {
        font-size: 16px;
        padding: 0 1.5rem;
    }
`;

function MainSecTitleButton(){
    return (
    <MainSection>
        <GlobalTitleSec2>
            ¿Quieres saber si tenemos cobertura de fibra en tu hogar?
        </GlobalTitleSec2>
        <SubTitle>
            Míralo aquí y contrata tu tarifa de fibra + móvil o solo fibra de forma sencilla
        </SubTitle>
    </MainSection>
    )
};

export default MainSecTitleButton;
