import { book } from "./NewBooks";
import styled from 'styled-components';
import {Title} from '../Title'
import RecommendationLetter from "../RecommendationLetter";
import {AngularImg} from '../../images/livro2.png'

const LatestReleasesContainer = styled.section`
    background-color: #EBECEE;
    padding-bottom: 20px;
    display: flex;
    flex-direction: column;
`

const NewBooksContainer = styled.div`
    margin-top: 30px;
    display: flex;
    width: 100%;
    justify-content: center;
    cursor: pointer;
`

function LatestReleases() {
    return (
    <LatestReleasesContainer>
            <Title 
                color="#EB9B00" 
                fontSize='2.25rem'>
                LATEST RELEASES
            </Title>
            <NewBooksContainer>
                {book.map( books => (<img src={books.src} alt="book-img" />))} 
            </NewBooksContainer>
            <RecommendationLetter 
                title='You might be interested in...'
                subtitle='Angular 11'
                description='Building an integrated application with the GO platform'
                img={AngularImg}
                alt='AngularImg'
            />
    </LatestReleasesContainer>
    )
}

export default LatestReleases
