import style from "styled-components";
import { useState } from 'react';
import PopDesign from '../PopUp';

const person = "person";
const shop = "shopping_cart";

const Icons = style.div`
    align-items: center;
    display: flex;
    justify-content: center;
`;

const IconList = style.div`
    align-items: center;
    cursor: pointer;
    display: flex;
    justify-content: center;
    width: 1.563rem;
    margin: 0 1.4rem;

    button {

        border: none;
        background-color: transparent;

        &:hover {
            color: #fff;
            cursor: pointer;
        }
    }
`;

function OptionsIconHeader() {

    const [open, setOpen] = useState(false);

    const popUpOpen = () => {
        setOpen(true);
    }

    const popUpClose = () => {
        setOpen(false);
    }


    return (
        <Icons> 
            <IconList>
                <button className="material-icons" onClick={popUpOpen}>{icon}</button>
            </IconList>


            {open && <PopDesign onClose={popUpClose}/>}
        </Icons>
    )
};

export default OptionsIconHeader;
