function RecommendationLetter({title, subtitle, description, img}) {
    return (
        <div>
            <h3>{title}</h3>
            <h4>{subtitle}</h4>
            <p>{description}</p>
            <img src={img} alt='AngularImg'/>
            <button>Learn more</button>
        </div>
    )
}

export default RecommendationLetter
