import { useState } from 'react';

export const usePopup = () => {
  const [open, setOpen] = useState(false);

  const popUpOpen = () => {
    setOpen(true);
  }

  const popUpClose = () => {
    setOpen(false);
  }

  return { open, popUpOpen, popUpClose };
}
