import styled from "styled-components";
const textOption = ['Category', 'Favorites', 'My bookshelf'];

const Options = styled.ul`
  display: flex;
`

const OptionsList = styled.li`
padding: 0 0.313rem;
min-width: 7.5rem;
cursor: pointer;
display: flex;
justify-content: center;
align-items: center;
text-align: center;
font-size: 1rem;
  height: 100%;
`

function OptionsHeader() {
  return (
    <ul className='Options'>
      {textOption.map(text => <li className='Options-list'><p>{text}</p></li>)}
    </ul>
  )
};

export default OptionsHeader;
