import styled from "styled-components";
import { useState } from "react";
import PopDesign from "../../../PopUp";
import { GlobalButton } from "../../../stylesGlobal";

const MainSection  = styled.div`
    text-align: center;
    width: 1000px;
`;

const Title = styled.h1`
    font-size: 2rem;
    font-weight: 900;
    text-align: center;
    text-transform: uppercase;
    margin: 2.5rem;
    line-height: 1.5;
`;

const SubTitle = styled.h2`
    text-transform: uppercase;
    font-weight: 400;
    margin: 0.5rem 0rem;
    font-size: 1.5rem;
    line-height: 1.5;
`;

const GlobalButtonCTA = styled(GlobalButton)`
width: 300px;
height: 65px;






margin: 1.5rem 0rem;



&:hover {
    background-color: #BFBFBF;
    color: black;        
  }

`

const Button = styled.button`
    
`;

function MainSecButtonCTA() {

    const [open, setOpen] = useState(false)

    const popUpOpen = () => {
        setOpen(true);
    }

    const popUpClose = () => {
        setOpen(false);
    }

    return (
        <MainSection >
            <Title>
                ¿Quieres saber si tenemos cobertura de fibra en tu hogar?
            </Title>
            <SubTitle>
                Míralo aquí y contrata tu tarifa de fibra +móvil o sólo fibra de forma sencilla
            </SubTitle>
            <Button onClick={popUpOpen}>
                Comprobar cobertura
            </Button>
            {open && <PopDesign onClose={popUpClose}/>}
        </MainSection >
    )
};

export { MainSecButtonCTA, Button };
