import { styled } from "styled-components";
import { GlobalCentralizeDiv } from "../../stylesGlobal";

const textOption = [
    { id: 1, name: 'Plan Amigo', link: '/friend' },
    { id: 2, name: 'Regalo', link: '/sport' },
    { id: 3, name: 'Preguntas', link: '/faq' },
];

const Nav = styled.nav`
    max-width: 1080px;
    margin: 0 auto;

    .desactive.line {
        border-top-color: transparent;
    }

    .desactive.line::after {
        transform: rotate(135deg);
    }

    .desactive.line::before {
        transform: rotate(-135deg);
        top: 7px;
    }
    
    .desactive.list {
        visibility: initial;
    }

    @media (max-width: 768px) {
    
        height: 50px;
        display: flex;
        align-items: center;
        justify-content: flex-end;

        desactive.line {
            border-top-color: transparent;
        }

        .desactive.line::after {
            transform: rotate(135deg);
        }

        .desactive.line::before {
            transform: rotate(-135deg);
            top: 7px;
        }
        
        .desactive.list {
            visibility: initial;
    }
    
}

`;

const Span = styled.span`
    @media (max-width: 768px) {
        border-top: 2px solid;
        width: 20px;

        &::before,
        &::after {
            content: "";
            display: block;
            width: 20px;
            height: 2px;
            background-color: currentColor;
            margin-top: 5px;
            position: relative;
            transition: .4s;
        }
    }
`;

const Button = styled.button`
    display: none;

    @media (max-width: 768px) {
        display: flex;
        padding: .5rem 1rem;
        font-size: 1rem;
        background: none;
        cursor: pointer;
        border: none;
        color: #000;
        margin-right: 1rem;
        align-items: center;
        justify-content: center;
    }
`;

const Ul = styled.ul`
    display: flex;
    list-style: none;

    @media (max-width: 768px) {
        display: block;
        position: absolute;
        width: 100 %;
        top: 65px;
        right: 0px;
        background-color: #020514;
        height: 100vh;
        text-align: center;
        visibility: hidden;

    



}

`;

const OptionsList = styled.li`
  padding: 0 2rem;
  cursor: pointer;
  display: flex;
  margin: 1rem auto;
  justify-content: center;
  align-items: center;
  list-style: none;

  a {
    text-decoration: none;
    color: #000;
    text-align: center;
    font-size: 16px;
    font-weight: 400;
    
    &:hover {
      color: #fff;
      cursor: pointer;
      scale: 1.15;
    }
  }

  @media (max-width: 768px) {
    
    a {
      display: none;
    }

    margin: 2rem;
    font-size: 24px;
  }

`;  

function HeaderMenu(props) {
    return (
        <GlobalCentralizeDiv>
            <Nav className="{">
                <Button>
                    <Span></Span>
                </Button>
                <Ul>
                    {textOption.map((text, indice) =>
                        <OptionsList key={indice}>
                            <a href={window.location.origin + text.link}>{text.name}</a>
                        </OptionsList>)}
                </Ul>
            </Nav>
        </GlobalCentralizeDiv>
    )
};

export default HeaderMenu;
