import styled from "styled-components"
import { Title } from "../Title"

const CardStyled = styled.div`
    align-items: center;
    background-color: #FFF;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 10px;
    display: flex;
    margin: 0 auto;
    max-width: 600px;
    padding: 25px 20px;
    justify-content: space-around;
    width: 100%;  
`

const ButtonStyled = styled.button`
    background-color: #EB9B00;
    color: #FFF;
    padding: 10px 0px;
    font-size: 16px;
    border: none;
    font-weight: 900;
    display: block;
    text-align: center;
    width: 150px;
    &:hover {
        cursor: pointer;
    }
`

const DescriptionStyled = styled.p`
    max-width: 300px;
`

const SubTitleStyled = styled.h4`
    color: #002F52;
    font-size: 18px;
    font-weight: bold;
    margin: 15px 0;
`

const ImgStyled = styled.img`
    width: 150px;
`

function RecommendationLetter({title, subtitle, description, img}) {
    return (
        <CardStyled>
            <Title color={"#EB9B00"} fontSize={"2.25rem"}>{title}</Title>
            <SubTitleStyled>{subtitle}</SubTitleStyled>
            <DescriptionStyled>{description}</DescriptionStyled>
            <ImgStyled src={img} alt='AngularImg'/>
            <ButtonStyled>Learn more</ButtonStyled>
        </CardStyled>
    )
}

export default RecommendationLetter
