import styled from "styled-components";
import { GlobalCentralizeDiv } from "../../stylesGlobal";
import { useState } from "react";

const textOption = [
  { id: 1, name: 'Plan Amigo', link: 'friend' },
  { id: 2, name: 'Regalo', link: 'sport' },
  { id: 3, name: 'Preguntas', link: 'faq' },
];

const OptionsList = styled.li`
  padding: 0 2rem;
  cursor: pointer;
  display: flex;
  justify-content: center;
  align-items: center;
  list-style: none;

  a {
    text-decoration: none;
    color: #000;
    text-align: center;
    font-size: 16px;
    font-weight: 400;
    
    &:hover {
      color: #fff;
      cursor: pointer;
    }
  }

  @media (max-width: 768px) {
    
    a {
      display: none;
    }

  }

`;

const Div = styled.div`
position: fixed;
top: 0;
left: 0;
bottom: 0;
right: 0;
background-color: white;
display: none;
z-index: 1;
flex-direction: column;
justify-content: center;
align-items: center;

  .material-icons {
    position: absolute;
    font-size: 28px;
    left: 45px;
    display: inline;
    text-decoration: none;
    color: #000;
  }

  @media (max-width: 768px) {
    .menu-items {
      display: ${(props) => (props.showMenu ? "block" : "none")};
      position: absolute;
      top: 60px;
      left: 0;
      width: 100%;
      background-color: #fff;
      padding: 10px;
      z-index: 1;
    }

    .material-icons {
      font-size: 28px;
      left: 15px;
    }
  }

  @media (max-width: 481px) {
    .material-icons {
      font-size: 24px;
      left: 25px;
    }

    .menu-items {
      top: 50px;
    }
  }
`;

const MenuItems = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
`;

const MenuItem = styled.li`
  margin: 10px 0;
`;

function OptionsHeader() {
  const [showMenu, setShowMenu] = useState(false);

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  return (
    <GlobalCentralizeDiv>
      <Div showMenu={showMenu}>
        <a href="/" className="material-icons" onClick={toggleMenu}>
          menu
        </a>
        <MenuItems className="menu-items">
          {textOption.map((text, indice) => (
            <MenuItem key={indice}>
              <a href={window.location.origin + text.link}>{text.name}</a>
            </MenuItem>
          ))}
        </MenuItems>
      </Div>
    </GlobalCentralizeDiv>
  );
}

export default OptionsHeader;
