import style from "styled-components";
import { useState } from 'react';
import PopDesign from '../PopUp';

const person = "person";
const shop = "shopping_cart";

const Icons = style.div`
    display: flex;
    align-items: center;
    justify-content: center;
`;

const IconList = style.div`
    align-items: center;
    cursor: pointer;
    display: flex;
    justify-content: center;

    button {
        margin: 1rem;
        border: none;
        background-color: transparent;

        &:hover {
            color: #fff;
            cursor: pointer;
        }
    }
`;

function OptionsIconHeader() {

    const [open, setOpen] = useState(false);

    const popUpOpen = () => {
        setOpen(true);
    }

    const popUpClose = () => {
        setOpen(false);
    }


    return (
       <>
            <Icons>
                <IconList>
                    <span className="material-icons">{person}</span>
                </IconList>
            </Icons>
        </> 
    )
};

export default OptionsIconHeader;
